import{_ as s,c as n,o as l,b as e}from"./chunks/framework.DUr976bL.js";const u=JSON.parse('{"title":"conda、anaconda","description":"","frontmatter":{"title":"conda、anaconda"},"headers":[{"level":2,"title":"下载与安装","slug":"下载与安装","link":"#下载与安装","children":[]},{"level":2,"title":"问题与方案","slug":"问题与方案","link":"#问题与方案","children":[]},{"level":2,"title":"命令与开发","slug":"命令与开发","link":"#命令与开发","children":[{"level":3,"title":"包安装","slug":"包安装","link":"#包安装","children":[]},{"level":3,"title":"本地训练模型相关操作","slug":"本地训练模型相关操作","link":"#本地训练模型相关操作","children":[]}]}],"relativePath":"aiart/machine-learning/conda.md","filePath":"aiart/machine-learning/conda.md"}'),p={name:"aiart/machine-learning/conda.md"};function o(r,a,c,t,i,d){return l(),n("div",null,a[0]||(a[0]=[e(`<h1 id="anaconda-数据科学工具包" tabindex="-1">Anaconda-数据科学工具包 <a class="header-anchor" href="#anaconda-数据科学工具包" aria-label="Permalink to &quot;Anaconda-数据科学工具包&quot;">​</a></h1><p>Anaconda：Unleash AI innovation and value。(释放人工智能的创新和价值)</p><h2 id="下载与安装" tabindex="-1">下载与安装 <a class="header-anchor" href="#下载与安装" aria-label="Permalink to &quot;下载与安装&quot;">​</a></h2><p><a href="https://www.anaconda.com/download" target="_blank" rel="noreferrer">官网下载地址</a></p><h2 id="问题与方案" tabindex="-1">问题与方案 <a class="header-anchor" href="#问题与方案" aria-label="Permalink to &quot;问题与方案&quot;">​</a></h2><ul><li><strong>notebook 忽然打不开</strong>，浏览器无法显示内容，好像是 js 错误。<br><strong>方案</strong>：重新下载包，重新安装。get！(conda update --all 跟新所有之后就不兼容，打不开了。只能重新安装)</li></ul><h2 id="命令与开发" tabindex="-1">命令与开发 <a class="header-anchor" href="#命令与开发" aria-label="Permalink to &quot;命令与开发&quot;">​</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;"># 查看所有包版本</span></span>
<span class="line"><span style="color:#B392F0;">conda</span><span style="color:#9ECBFF;"> list</span></span>
<span class="line"><span style="color:#6A737D;"># 计算行数，得到包的总数量-1，包含了标题</span></span>
<span class="line"><span style="color:#B392F0;">conda</span><span style="color:#9ECBFF;"> list</span><span style="color:#F97583;"> |</span><span style="color:#B392F0;"> wc</span><span style="color:#79B8FF;"> -l</span></span>
<span class="line"><span style="color:#6A737D;"># 查看某个包版本</span></span>
<span class="line"><span style="color:#B392F0;">conda</span><span style="color:#9ECBFF;"> list</span><span style="color:#9ECBFF;"> xxx</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><h3 id="包安装" tabindex="-1">包安装 <a class="header-anchor" href="#包安装" aria-label="Permalink to &quot;包安装&quot;">​</a></h3><p>有些工具包名字，在 <code>anaconda</code> 导航搜索不到。例如 <code>eli5</code>。那就如下安装：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#B392F0;">conda</span><span style="color:#9ECBFF;"> install</span><span style="color:#79B8FF;"> -c</span><span style="color:#9ECBFF;"> conda-forge</span><span style="color:#9ECBFF;"> eli5</span></span>
<span class="line"><span style="color:#6A737D;"># 或pip安装</span></span>
<span class="line"><span style="color:#B392F0;">pip</span><span style="color:#9ECBFF;"> install</span><span style="color:#9ECBFF;"> eli5</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h3 id="本地训练模型相关操作" tabindex="-1">本地训练模型相关操作 <a class="header-anchor" href="#本地训练模型相关操作" aria-label="Permalink to &quot;本地训练模型相关操作&quot;">​</a></h3><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;"># 列出所有虚拟环境</span></span>
<span class="line"><span style="color:#B392F0;">conda</span><span style="color:#9ECBFF;"> env</span><span style="color:#9ECBFF;"> list</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A737D;"># 创建和激活虚拟环境</span></span>
<span class="line"><span style="color:#B392F0;">conda</span><span style="color:#9ECBFF;"> create</span><span style="color:#79B8FF;"> -n</span><span style="color:#9ECBFF;"> lora-env</span><span style="color:#9ECBFF;"> python=</span><span style="color:#79B8FF;">3.9</span></span>
<span class="line"><span style="color:#B392F0;">conda</span><span style="color:#9ECBFF;"> activate</span><span style="color:#9ECBFF;"> lora-env</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A737D;"># 包更新</span></span>
<span class="line"><span style="color:#B392F0;">pip</span><span style="color:#9ECBFF;"> list</span><span style="color:#79B8FF;"> --outdated</span></span>
<span class="line"><span style="color:#B392F0;">pip</span><span style="color:#9ECBFF;"> install</span><span style="color:#79B8FF;"> --upgrade</span><span style="color:#9ECBFF;"> package_name</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br></div></div>`,13)]))}const F=s(p,[["render",o]]);export{u as __pageData,F as default};
