import{_ as s,c as n,o as a,a as e}from"./app.95a93399.js";const C=JSON.parse('{"title":"基础命令","description":"","frontmatter":{"title":"基础命令","outline":"deep"},"headers":[{"level":2,"title":"find 文件名查找","slug":"find-文件名查找","link":"#find-文件名查找","children":[]},{"level":2,"title":"which 命令查找","slug":"which-命令查找","link":"#which-命令查找","children":[]},{"level":2,"title":"whereis 命令查找","slug":"whereis-命令查找","link":"#whereis-命令查找","children":[]},{"level":2,"title":"grep 内容查找","slug":"grep-内容查找","link":"#grep-内容查找","children":[]},{"level":2,"title":"locate 文件名查找","slug":"locate-文件名查找","link":"#locate-文件名查找","children":[]},{"level":2,"title":"du：disk usage 目录/文件大小","slug":"du-disk-usage-目录-文件大小","link":"#du-disk-usage-目录-文件大小","children":[{"level":3,"title":"-d 迭代深度","slug":"d-迭代深度","link":"#d-迭代深度","children":[]},{"level":3,"title":"-h 人类可读","slug":"h-人类可读","link":"#h-人类可读","children":[]},{"level":3,"title":"-s 文件大小总计","slug":"s-文件大小总计","link":"#s-文件大小总计","children":[]},{"level":3,"title":"|：pipe 管道命令","slug":"pipe-管道命令","link":"#pipe-管道命令","children":[]}]},{"level":2,"title":"echo 打印信息","slug":"echo-打印信息","link":"#echo-打印信息","children":[]},{"level":2,"title":"目录参考","slug":"目录参考","link":"#目录参考","children":[{"level":3,"title":"顶级目录","slug":"顶级目录","link":"#顶级目录","children":[]},{"level":3,"title":"专用目录（有时并不存在）","slug":"专用目录-有时并不存在","link":"#专用目录-有时并不存在","children":[]},{"level":3,"title":"目录结构图","slug":"目录结构图","link":"#目录结构图","children":[]}]}],"relativePath":"mac/linux/os-command.md"}'),l={name:"mac/linux/os-command.md"},p=e(`<h1 id="基础命令" tabindex="-1">基础命令 <a class="header-anchor" href="#基础命令" aria-hidden="true">#</a></h1><p>man xxx 查看帮助手册(如查看 du：man du)。</p><h2 id="find-文件名查找" tabindex="-1">find 文件名查找 <a class="header-anchor" href="#find-文件名查找" aria-hidden="true">#</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">find</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">.</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-iname</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">*google*</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-d</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># . 当前位置；/ 根目录</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># i 不区分大小写；name 要搜索的文件名，后面紧跟文件名</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># d 查找深度设置为 1，默认好像是无限递归查询？</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h2 id="which-命令查找" tabindex="-1">which 命令查找 <a class="header-anchor" href="#which-命令查找" aria-hidden="true">#</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#82AAFF;">which</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">brew</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># /opt/homebrew/bin/brew</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="whereis-命令查找" tabindex="-1">whereis 命令查找 <a class="header-anchor" href="#whereis-命令查找" aria-hidden="true">#</a></h2><p>查找结果除了显示命令所在的命令以外，还会列出帮助文档所在的目录。</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">where</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">brew</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># /opt/homebrew/bin/brew</span></span>
<span class="line"><span style="color:#FFCB6B;">whereis</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">brew</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># brew: /opt/homebrew/bin/brew /opt/homebrew/share/man/man1/brew.1</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h2 id="grep-内容查找" tabindex="-1">grep 内容查找 <a class="header-anchor" href="#grep-内容查找" aria-hidden="true">#</a></h2><p>global regular expression</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">grep</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-i</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-n</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">python</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">.zshrc</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># -i:忽略大小写 -n:显示行号 目标内容 目标文件</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 104:alias python=&quot;/usr/local/bin/python3&quot;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h2 id="locate-文件名查找" tabindex="-1">locate 文件名查找 <a class="header-anchor" href="#locate-文件名查找" aria-hidden="true">#</a></h2><p><code>find</code> 遍历磁盘查找文件，占多资源，相对较慢。而 <code>locate</code> 命令在 Linux 文件数据库中查找，速度快。MacOs 貌似默认没有这个。</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">locate</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-i</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">*google*</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="du-disk-usage-目录-文件大小" tabindex="-1">du：disk usage 目录/文件大小 <a class="header-anchor" href="#du-disk-usage-目录-文件大小" aria-hidden="true">#</a></h2><p>用于显示目录或文件的大小。</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#676E95;font-style:italic;"># colab notebook 根目录 磁盘使用查询</span></span>
<span class="line"><span style="color:#89DDFF;">!</span><span style="color:#FFCB6B;">du</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-ahd</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">sort</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-hr</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="d-迭代深度" tabindex="-1">-d 迭代深度 <a class="header-anchor" href="#d-迭代深度" aria-hidden="true">#</a></h3><p><code>-d</code> 表示迭代深度，当前目录深度是 <code>0</code>，<code>1</code> 表示最深迭代到当前目录的下一个深度，等价的命令是 <code>du --max-depth 1</code>。</p><h3 id="h-人类可读" tabindex="-1">-h 人类可读 <a class="header-anchor" href="#h-人类可读" aria-hidden="true">#</a></h3><p><code>-h</code> 或 <code>--human-readable</code> 以 <code>K，M，G</code> 为单位，提高信息的可读性。</p><h3 id="s-文件大小总计" tabindex="-1">-s 文件大小总计 <a class="header-anchor" href="#s-文件大小总计" aria-hidden="true">#</a></h3><p><code>-s</code> 或 <code>--summarize</code> 仅显示总计，包含子目录。</p><h3 id="pipe-管道命令" tabindex="-1">|：<code>pipe 管道命令</code> <a class="header-anchor" href="#pipe-管道命令" aria-hidden="true">#</a></h3><ul><li>选取命令：<code>cut、grep</code></li><li>排序命令：<code>sort、wc、uniq</code><br><code>-r 将排序结果逆序</code></li></ul><h2 id="echo-打印信息" tabindex="-1">echo 打印信息 <a class="header-anchor" href="#echo-打印信息" aria-hidden="true">#</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> $SHELL </span><span style="color:#676E95;font-style:italic;"># 查看当前shell</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># /bin/zsh</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="目录参考" tabindex="-1">目录参考 <a class="header-anchor" href="#目录参考" aria-hidden="true">#</a></h2><p>在 Linux 系统中，文件系统是树状结构，每个目录都有特定的用途。以下是常见 Linux 目录及其用途的说明：</p><h3 id="顶级目录" tabindex="-1">顶级目录 <a class="header-anchor" href="#顶级目录" aria-hidden="true">#</a></h3><ul><li><p><strong><code>/</code></strong>： 根目录。文件系统的起点。所有其他目录和文件都是从根目录派生的。</p></li><li><p><strong><code>/bin</code></strong>： 二进制可执行文件目录。存放基本用户命令，如 <code>ls</code>、<code>cp</code>、<code>mv</code> 等，适合单用户模式下的基本操作。</p></li><li><p><strong><code>/boot</code></strong>： 引导加载器文件目录。包含启动 Linux 系统所需的文件，如内核映像 (<code>vmlinuz</code>)、引导加载器配置文件等。</p></li><li><p><strong><code>/dev</code></strong>： 设备文件目录。包含代表系统设备的文件，如硬盘、终端、音频设备等。例如 <code>/dev/sda</code> 代表一个硬盘设备。</p></li><li><p><strong><code>/etc</code></strong>： 配置文件目录。包含系统管理和配置文件，如网络配置、服务配置、用户密码文件等。</p></li><li><p><strong><code>/home</code></strong>： 用户家目录。每个用户在此目录下有自己的子目录，用于存储个人文件和配置。</p></li><li><p><strong><code>/lib</code></strong>： 库文件目录。包含系统和应用程序使用的共享库文件，以及加载这些库的内核模块。</p></li><li><p><strong><code>/media</code></strong>： 可移动媒体挂载点。用于挂载可移动设备（如 CD、USB 闪存驱动器）。</p></li><li><p><strong><code>/mnt</code></strong>： 临时挂载点。系统管理员可以用来临时挂载文件系统。</p></li><li><p><strong><code>/opt</code></strong>： 可选应用程序目录。用于安装附加的应用程序包，常用于第三方软件。</p></li><li><p><strong><code>/proc</code></strong>： 进程和内核信息虚拟文件系统。提供内核、进程信息，常用于系统监控和管理。</p></li><li><p><strong><code>/root</code></strong>： 根用户的家目录。超级用户（root）登录后的默认目录。</p></li><li><p><strong><code>/run</code></strong>： 运行时文件系统。存放系统启动后生成的临时文件，如进程 PID 文件和套接字文件。</p></li><li><p><strong><code>/sbin</code></strong>： 系统管理二进制文件目录。包含系统管理员使用的工具，如 <code>fdisk</code>、<code>ifconfig</code> 等。</p></li><li><p><strong><code>/srv</code></strong>： 服务数据目录。用于存放本地服务提供的数据（如网页服务器、FTP 服务器的数据）。</p></li><li><p><strong><code>/sys</code></strong>： 系统信息虚拟文件系统。提供有关硬件、设备驱动程序的信息。</p></li><li><p><strong><code>/tmp</code></strong>： 临时文件目录。用于存放临时文件，系统会定期清理这个目录。</p></li><li><p><strong><code>/usr</code></strong>： 用户二级文件系统。包含用户的二进制文件、库文件、文档、和其他文件。</p><ul><li><strong><code>/usr/bin</code></strong>：非基本用户命令的二进制文件，如 <code>gcc</code>。</li><li><strong><code>/usr/sbin</code></strong>：非基本系统管理二进制文件，如 <code>httpd</code>。</li><li><strong><code>/usr/lib</code></strong>：非基本库文件。</li><li><strong><code>/usr/local</code></strong>：本地安装的软件和脚本，常用于系统管理员安装的程序。</li><li><strong><code>/usr/share</code></strong>：共享数据，如 man 手册页、文档等。</li></ul></li><li><p><strong><code>/var</code></strong>： 变量数据目录。用于存放经常变化的数据，如日志文件、缓存文件、邮件队列、打印队列等。</p><ul><li><strong><code>/var/log</code></strong>：日志文件。</li><li><strong><code>/var/spool</code></strong>：任务队列数据，如邮件、打印任务。</li><li><strong><code>/var/tmp</code></strong>：用于长时间保留的临时文件。</li></ul></li></ul><h3 id="专用目录-有时并不存在" tabindex="-1">专用目录（有时并不存在） <a class="header-anchor" href="#专用目录-有时并不存在" aria-hidden="true">#</a></h3><ul><li><p><strong><code>/lost+found</code></strong>： 恢复目录。用于文件系统检查过程中恢复的文件。</p></li><li><p><strong><code>/snap</code></strong>： Snap 软件包的安装目录。主要用于 Canonical 的 Ubuntu 系统上。</p></li></ul><h3 id="目录结构图" tabindex="-1">目录结构图 <a class="header-anchor" href="#目录结构图" aria-hidden="true">#</a></h3><div class="language-plaintext line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">plaintext</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">/</span></span>
<span class="line"><span style="color:#A6ACCD;">├── bin</span></span>
<span class="line"><span style="color:#A6ACCD;">├── boot</span></span>
<span class="line"><span style="color:#A6ACCD;">├── dev</span></span>
<span class="line"><span style="color:#A6ACCD;">├── etc</span></span>
<span class="line"><span style="color:#A6ACCD;">├── home</span></span>
<span class="line"><span style="color:#A6ACCD;">├── lib</span></span>
<span class="line"><span style="color:#A6ACCD;">├── media</span></span>
<span class="line"><span style="color:#A6ACCD;">├── mnt</span></span>
<span class="line"><span style="color:#A6ACCD;">├── opt</span></span>
<span class="line"><span style="color:#A6ACCD;">├── proc</span></span>
<span class="line"><span style="color:#A6ACCD;">├── root</span></span>
<span class="line"><span style="color:#A6ACCD;">├── run</span></span>
<span class="line"><span style="color:#A6ACCD;">├── sbin</span></span>
<span class="line"><span style="color:#A6ACCD;">├── srv</span></span>
<span class="line"><span style="color:#A6ACCD;">├── sys</span></span>
<span class="line"><span style="color:#A6ACCD;">├── tmp</span></span>
<span class="line"><span style="color:#A6ACCD;">├── usr</span></span>
<span class="line"><span style="color:#A6ACCD;">│   ├── bin</span></span>
<span class="line"><span style="color:#A6ACCD;">│   ├── lib</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br></div></div>`,36),o=[p];function i(r,c,t,d,h,b){return a(),n("div",null,o)}const g=s(l,[["render",i]]);export{C as __pageData,g as default};
