import{_ as s,c as n,o as a,a as l}from"./app.7a9633d7.js";const C=JSON.parse('{"title":"npm命令及其使用方法","description":"","frontmatter":{"title":"npm命令及其使用方法"},"headers":[{"level":2,"title":"创建package.json文件","slug":"创建package-json文件","link":"#创建package-json文件","children":[]},{"level":2,"title":"发布 npm 包","slug":"发布-npm-包","link":"#发布-npm-包","children":[]},{"level":2,"title":"nrm 管理 npm 源地址","slug":"nrm-管理-npm-源地址","link":"#nrm-管理-npm-源地址","children":[]},{"level":2,"title":"设置源地址","slug":"设置源地址","link":"#设置源地址","children":[]},{"level":2,"title":"安装指定版本","slug":"安装指定版本","link":"#安装指定版本","children":[]},{"level":2,"title":"版本号及标识","slug":"版本号及标识","link":"#版本号及标识","children":[{"level":3,"title":"版本号","slug":"版本号","link":"#版本号","children":[]},{"level":3,"title":"标识","slug":"标识","link":"#标识","children":[]}]}],"relativePath":"node/npm.md"}'),e={name:"node/npm.md"},p=l(`<h1 id="node-package-manager" tabindex="-1">Node Package Manager <a class="header-anchor" href="#node-package-manager" aria-hidden="true">#</a></h1><h2 id="创建package-json文件" tabindex="-1">创建<code>package.json</code>文件 <a class="header-anchor" href="#创建package-json文件" aria-hidden="true">#</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 交互式创建</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">init</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 快速创建默认项和值的文件</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">init</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-y</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h2 id="发布-npm-包" tabindex="-1">发布 npm 包 <a class="header-anchor" href="#发布-npm-包" aria-hidden="true">#</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 查看源地址</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">registry</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 如不是npm官方源</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 则切换到官方源地址</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">config</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">set</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">registry</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://registry.npmjs.org</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 查看是否已登录npm，如已登录则会显示用户名</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">who</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">am</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">i</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 如果没有登录，先登录，然后输入用户名、密码</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">login</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 发布</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">publish</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 如果包名称带有scope(如@wswplay)，则可能会发布失败</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># You must sign up for private packages 意思是必须付费</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 但其实并不必须，发布为公共访问就行</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">publish</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--access</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">public</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 发布成功</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br></div></div><p>发布成功后，登录<a href="https://www.npmjs.com/" target="_blank" rel="noreferrer">npm 官网</a>个人中心<code>packages</code>中可以看到。</p><h2 id="nrm-管理-npm-源地址" tabindex="-1">nrm 管理 npm 源地址 <a class="header-anchor" href="#nrm-管理-npm-源地址" aria-hidden="true">#</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 全局安装nrm</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-g</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">nrm</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 如下安装 nrm ls时，当前使用库前面会带*</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">Pana/nrm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-g</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 查看可选的源地址</span></span>
<span class="line"><span style="color:#FFCB6B;">nrm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ls</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># npm ---------- https://registry.npmjs.org/</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># yarn --------- https://registry.yarnpkg.com/</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># tencent ------ https://mirrors.cloud.tencent.com/npm/</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># cnpm --------- https://r.cnpmjs.org/</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># * taobao ----- https://registry.npmmirror.com/</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># npmMirror ---- https://skimdb.npmjs.com/registry/</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 查看当前正在使用的库 You are using taobao registry.</span></span>
<span class="line"><span style="color:#FFCB6B;">nrm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">current</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 切换到淘宝源</span></span>
<span class="line"><span style="color:#FFCB6B;">nrm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">use</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">taobao</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 测试速度</span></span>
<span class="line"><span style="color:#FFCB6B;">nrm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">test</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">npm</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br></div></div><h2 id="设置源地址" tabindex="-1">设置源地址 <a class="header-anchor" href="#设置源地址" aria-hidden="true">#</a></h2><blockquote><p>有些项目，有些时候，我们一定要切到官方的源上，才能安装。如果不，会提示一些莫名其妙的错误。</p></blockquote><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 查看源地址</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">registry</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 将源地址改成taobao源</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">config</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">set</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">registry</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://registry.npm.taobao.org</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 恢复成官方源地址</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">config</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">set</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">registry</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://registry.npmjs.org</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><h2 id="安装指定版本" tabindex="-1">安装指定版本 <a class="header-anchor" href="#安装指定版本" aria-hidden="true">#</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 安装Vue3.0及以上版本</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">i</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">vue@^3.0.0</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 安装最新版本</span></span>
<span class="line"><span style="color:#FFCB6B;">npm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">i</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">xxx@latest</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-D</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h2 id="版本号及标识" tabindex="-1">版本号及标识 <a class="header-anchor" href="#版本号及标识" aria-hidden="true">#</a></h2><h3 id="版本号" tabindex="-1">版本号 <a class="header-anchor" href="#版本号" aria-hidden="true">#</a></h3><p><code>npm</code>包版本号通用遵循<code>semver</code>语义化版本规范，版本格式为(如<code>1.0.0</code>)：<code>major.minor.patch</code></p><div class="tip custom-block"><p class="custom-block-title">版本号</p><p>1、主版本号(<code>major</code>)：当你做了不兼容的 API 修改<br> 2、次版本号(<code>minor</code>)：当你做了向下兼容的功能性新增<br> 3、修订号(<code>patch</code>)：当你做了向下兼容的问题修正</p></div><p>先行版本号，是加到修订号的后面，作为版本号的延伸。</p><blockquote><p>格式是在修订版本号后面加上一个连接号（-），再加上一连串以点（.）分割的标识符，标识符可以由英文、数字和连接号（[0-9A-Za-z-]）组成。</p></blockquote><div class="tip custom-block"><p class="custom-block-title">先行号</p><p>1、<code>alpha</code>：不稳定版本，一般而言，该版本的 Bug 较多，需要继续修改，是测试版本<br> 2、<code>beta</code>：基本稳定，相对于 Alpha 版已经有了很大的进步，消除了严重错误<br> 3、<code>rc</code>：和正式版基本相同，基本上不存在导致错误的 Bug<br> 4、<code>release</code>：最终版本</p></div><h3 id="标识" tabindex="-1">标识 <a class="header-anchor" href="#标识" aria-hidden="true">#</a></h3><p><code>package.json</code>中版本号常有<code>^、~</code>或者<code>&gt;=</code>等标识符:</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><code>插入符号^</code>：固定主版本号，次版本号和修订号可以随意更改，例如^2.0.0，可以使用 2.0.1、2.2.2 、2.9.9 的版本。<br><code>波浪符号~</code>：固定主版本号和次版本号，修订号可以随意更改，例如~2.0.0，可以使用 2.0.0、2.0.2 、2.0.9 的版本。<br><code>对比符号类的</code>：<code>&gt;</code>(大于) <code>&gt;=</code>(大于等于) <code>&lt;</code>(小于) <code>&lt;=</code>(小于等于)<br><code>或符号||</code>：可以用来设置多个版本号限制规则，例如 &gt;= 3.0.0 || &lt;= 1.0.0<br><code>没有任何符号</code>：完全百分百匹配，必须使用当前版本号<br><code>任意版本</code>：对版本没有限制，一般不用</p></div>`,23),o=[p];function c(r,t,i,y,d,b){return a(),n("div",null,o)}const u=s(e,[["render",c]]);export{C as __pageData,u as default};
